const assert = require('assert');
const changeExt = require('..');

describe('changeExt', function () {

	[
		['', '.a', ''],
		['a', '.b', 'a.b'],
		['a.b', '.c', 'a.c'],
		['a.b', 'c', 'ac'],
		['a.b.c', '.d', 'a.b.d'],
		['a/b/c.d.e', '.f.g', 'a/b/c.d.f.g'],
		['a/b/c.d.e.f', '', 'a/b/c.d.e']
	].forEach(([filePath, newExt, expected]) => {
		it(`should return "${expected}" from ("${filePath}", "${newExt}")`, function () {
			assert.equal(changeExt(filePath, newExt), expected);
		});
	});

});
