/**
 * @param  {Number} size
 * @return {String}
 * @example
 * fileSize(50)
 * // '50bytes'
 * fileSize(5500000)
 * // '5.5MB'
 */
function fileSize(size) {
	let unit;
	size = Math.floor(parseFloat(size));
	if (size < 2) {
		unit = 'byte';
	} else if (size < 1000) {
		unit = 'bytes';
	} else {
		const order = Math.floor(Math.log10(size) / 3);
		unit = `${('kMGTPE').charAt(order - 1)}B`;
		size = (Math.floor(size / Math.pow(1000, order) * 10) / 10).toFixed(1);
	}
	return `${size}${unit}`;
}

module.exports = fileSize;
