const assert = require('assert');
const fileSize = require('..');

describe('fileSize', function () {

	it('should return "0byte"', function () {
		assert.equal(fileSize(0), '0byte');
	});

	it('should return "1byte"', function () {
		assert.equal(fileSize(1), '1byte');
	});

	it('should return "2bytes"', function () {
		assert.equal(fileSize(2), '2bytes');
	});

	it('should return "999bytes"', function () {
		assert.equal(fileSize(999), '999bytes');
	});

	it('should return "1.0kB"', function () {
		assert.equal(fileSize(1000), '1.0kB');
	});

	it('should return "999.9kB"', function () {
		assert.equal(fileSize(999999.9), '999.9kB');
	});

	it('should return "1.0MB"', function () {
		assert.equal(fileSize(1000000), '1.0MB');
	});

	it('should return "999.9MB"', function () {
		assert.equal(fileSize(999999999.9), '999.9MB');
	});

	it('should return "1.0GB"', function () {
		assert.equal(fileSize(1000000000), '1.0GB');
	});

});
