const leftpad = require('../leftpad');

function getOffsetString(date) {
	const offset = date.getTimezoneOffset ? date.getTimezoneOffset() : date;
	const absOffset = Math.abs(offset);
	const sign = offset < 0 ? '+' : '-';
	const offsetHours = leftpad(Math.floor(absOffset / 60), 2);
	const offsetMinutes = leftpad(absOffset % 60, 2);
	return `${sign}${offsetHours}:${offsetMinutes}`;
}

module.exports = getOffsetString;
