const assert = require('assert');
const getOffsetString = require('../getOffsetString');

describe('getOffsetString', function () {

	it('should return timezone offset +10:00', function () {
		assert.equal(getOffsetString(-600), '+10:00');
	});

	it('should return timezone offset -01:23', function () {
		assert.equal(getOffsetString(+83), '-01:23');
	});

});
