const assert = require('assert');
const getLast = require('..');

describe('getLast', function () {

	it('should return the last item of an array', function () {
		assert.equal(getLast([1, 2, 3]), 3);
	});

	it('should return the last item of an arguments object', function () {
		function getArguments() {
			return arguments;
		}
		const args = getArguments(4, 5, 6);
		assert.equal(getLast(args), 6);
	});

	it('should return the last item of an arguments object', function () {
		assert.equal(getLast({
			2: 9,
			length: 3
		}), 9);
	});

	it('should return the last item of an array-like object', function () {
		function *seq(from = 0, to = 0) {
			var index = from;
			while (index <= to) {
				yield index++;
			}
		}
		const iterable = seq(0, 100);
		assert.equal(getLast(iterable), 100);
	});

});
