const assert = require('assert');
const isNumber = require('..');

describe('isNumber', function () {

	[
		[true, false, 'true'],
		[false, false, 'false'],
		[null, false, 'null'],
		[undefined, false, 'undefined'],
		[0, true, '0'],
		[1, true, '1'],
		[NaN, true, 'NaN'],
		['', false, '""'],
		['text', false, '"text"'],
		[[], false, '[]'],
		[{}, false, '{}'],
		[() => {}, false, '() => {}']
	].forEach(([testee, expected, text]) => {
		it(`should return ${expected} if the argument is ${text}`, function () {
			assert.equal(isNumber(testee), expected);
		});
	});

});
