const assert = require('assert');
const isObject = require('..');

describe('isObject', function () {

	[
		[true, false, 'true'],
		[false, false, 'false'],
		[null, false, 'null'],
		[undefined, false, 'undefined'],
		[0, false, '0'],
		[1, false, '1'],
		[NaN, false, 'NaN'],
		['', false, '""'],
		['text', false, '"text"'],
		[[], true, '[]'],
		[{
			0: 0,
			length: 1
		}, true, '{}'],
		[{}, true, '{}'],
		[() => {}, true, '() => {}']
	].forEach(([testee, expected, text]) => {
		it(`should return ${expected} if the argument is ${text}`, function () {
			assert.equal(isObject(testee), expected);
		});
	});

});
