const assert = require('assert');
const isString = require('..');

describe('isString', function () {

	[
		[true, false, 'true'],
		[false, false, 'false'],
		[null, false, 'null'],
		[undefined, false, 'undefined'],
		[0, false, '0'],
		[1, false, '1'],
		[NaN, false, 'NaN'],
		['', true, '""'],
		['text', true, '"text"'],
		[[], false, '[]'],
		[{
			0: 0,
			length: 1
		}, false, '{}'],
		[{}, false, '{}'],
		[() => {}, false, '() => {}']
	].forEach(([testee, expected, text]) => {
		it(`should return ${expected} if the argument is ${text}`, function () {
			assert.equal(isString(testee), expected);
		});
	});

});
