const assert = require('assert');
const isUndefined = require('..');

describe('isUndefined', function () {

	[
		[true, false, 'true'],
		[false, false, 'false'],
		[null, false, 'null'],
		[undefined, true, 'undefined'],
		[0, false, '0'],
		[1, false, '1'],
		[NaN, false, 'NaN'],
		['', false, '""'],
		['text', false, '"text"'],
		[[], false, '[]'],
		[{}, false, '{}'],
		[() => {}, false, '() => {}']
	].forEach(([testee, expected, text]) => {
		it(`should return ${expected} if the argument is ${text}`, function () {
			assert.equal(isUndefined(testee), expected);
		});
	});

});
