class Labeler {

	constructor (name = '') {
		this.name = name;
		this.items = [];
	}

	get length () {
		return this.items.length;
	}

	get (index) {
		return this.items[index];
	}

	label (value) {
		const dictionary = this.items;
		let index = dictionary.indexOf(value);
		if (index < 0) {
			index = dictionary.push(value) - 1;
		}
		return index;
	}

	toString () {
		return this.items.map((item, index) => {
			return `${index}: ${item}`;
		}).join('\n');
	}

}

module.exports = new Labeler();
module.exports.Labeler = Labeler;
