const assert = require('assert');
const onError = require('..');

describe('onError', function () {
	it('should listen an error', function (done) {
		onError(new Error('123'), (error) => {
			assert.equal(/123/.test(error), true);
			done();
		});
	});
	it('should create an error if nothing is passed', function (done) {
		const params = {};
		onError(params.undefined, (error) => {
			assert.equal(error instanceof Error, true);
			done();
		});
	});
});
