const assert = require('assert');
const removeBOM = require('..');

describe('removeBOM', function () {

	it('should remove BOM from a buffer', function () {
		assert.deepEqual(removeBOM(Buffer.from([0xef, 0xbb, 0xbf])), Buffer.from([]));
	});

	it('should remove BOM from a string', function () {
		assert.equal(removeBOM(Buffer.from([0xef, 0xbb, 0xbf]).toString('') + 'DJ Octavio'), 'DJ Octavio');
	});

	it('should keep the buffer unchanged if it has no BOM', function () {
		assert.deepEqual(removeBOM(Buffer.from('DJ Octavio')), Buffer.from('DJ Octavio'));
	});

	it('should keep the string unchanged if it has no BOM', function () {
		assert.equal(removeBOM('DJ Octavio'), 'DJ Octavio');
	});

});
