const assert = require('assert');
const chomp = require('..');

describe('trim', function () {

	it('should return an empty string', function () {
		assert.equal(chomp('\n             \r  \t  '), '');
	});

	it('should remove spaces on the both ends', function () {
		assert.equal(chomp('\n\tZapfish \n'), 'Zapfish');
	});

});
